package com.jksh.domain.service;import com.jksh.application.common.model.Page;import com.jksh.application.common.model.QueryConditionForPosition;import com.jksh.application.common.model.QueryParam;import com.jksh.application.common.param.dto.PositionDTO;import com.jksh.application.common.param.vo.PositionVO;import com.jksh.infrastructure.repository.po.RhSysUser;public interface PositionService {    /**     * create a new position     * @param positionDTO     * @return     */    Boolean createPosition(Integer userId, PositionDTO positionDTO);        /**     * delete a position by position id     * @param id     * @return     */    Boolean deletePositionById(RhSysUser user, Integer id);    /**     * update a position by position id     * @param user     * @param positionDTO     * @return     */    Boolean updatePositionById(RhSysUser user, PositionDTO positionDTO);    /**     * get the position of all     * @param queryParam     * @return     */    Page getAllPositions(QueryParam queryParam, QueryConditionForPosition queryConditionForPosition);        /**     * get the position by id     * @param positionId     * @return     */    PositionVO getPositionById(Integer positionId);}