package com.jksh.domain.service;import com.jksh.application.common.model.Page;import com.jksh.application.common.model.QueryConditionForResume;import com.jksh.application.common.model.QueryParam;import com.jksh.application.common.param.dto.ResumeDTO;import com.jksh.application.common.param.vo.ResumeVO;import com.jksh.infrastructure.repository.po.RhSysUser;import org.springframework.web.multipart.MultipartFile;public interface ResumeService {    /**     * 创建简历     * @param userId     * @param resumeDTO     * @return     */    Boolean createResume(Integer userId, ResumeDTO resumeDTO);    /**     * 上传简历     * @param user     * @param file     * @return     */    ResumeVO uploadFile(RhSysUser user, MultipartFile file);    /**     * 修改简历     * @param userId     * @param resumeDTO     * @return     */    Boolean updateResume(Integer userId, ResumeDTO resumeDTO);    /**     * 删除简历     * @param userId     * @param resumeId     * @return     */    Boolean deleteResume(Integer userId, Integer resumeId);    /**     * 查询列表     * @param user     * @param queryConditionForResume     * @return     */    Page queryPageOfResume(RhSysUser user, QueryParam queryParam, QueryConditionForResume queryConditionForResume);}