package com.jksh.domain.service;import com.jksh.application.common.model.Page;import com.jksh.application.common.model.QueryParam;import com.jksh.application.common.param.dto.UserEntrienceDTO;import com.jksh.infrastructure.repository.po.RhSysUser;public interface UserService {    /**     * user register interface     * @param userEntrienceDTO     * @return     */    Boolean userRegist(UserEntrienceDTO userEntrienceDTO);    /**     * login interface, also need to check the password     * @param userEntrienceDTO     * @return     */    RhSysUser userLogin(UserEntrienceDTO userEntrienceDTO);        Boolean verifyUser(RhSysUser currentUser, Integer userId, Integer operation);    /**     * get user by username     * @param username     * @return     */    RhSysUser getByUsername(String username);    /**     * is user exist     * @param username     * @return     */    Boolean isExist(String username);    /**     * get all general users , not admin     * @param queryParam     * @return     */    Page getAllGeneralUser(QueryParam queryParam);    }