package com.letgee.application.controller;import com.letgee.application.common.enums.HttpCodeEnum;import com.letgee.domain.event.Const;import com.letgee.infrastructure.repository.po.RhSysUser;import com.letgee.infrastructure.utils.TokenUtils;import org.apache.commons.collections4.CollectionUtils;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;public class BaseController {    public static Map<Object, HttpCodeEnum> systemMap = new ConcurrentHashMap<>();    @Autowired    protected TokenUtils tokenUtils;        protected Boolean existAdmin(RhSysUser user){        return StringUtils.equals(Const.ADMIN_TYPE, user.getUserType());    }        protected Boolean hasUserActive(RhSysUser user){        return StringUtils.equals(Const.ACTIVE_TYPE, user.getStatus());    }}