package com.letgee.application.controller;import com.letgee.application.common.enums.HttpCodeEnum;import com.letgee.application.common.exception.ServerException;import com.letgee.application.common.param.result.ResponseResult;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ResponseBody;import javax.servlet.http.HttpServletRequest;@org.springframework.web.bind.annotation.ControllerAdvicepublic class ControllerAdvice {    private static final Logger logger = LoggerFactory.getLogger(ControllerAdvice.class);        @ExceptionHandler(value = Exception.class)    @ResponseBody    public ResponseResult bizExceptionHanlder(HttpServletRequest request, ServerException exception){        logger.error(exception.getErrorMsg());        return ResponseResult.error(exception.getErrorCode(),exception.getErrorMsg());    }    }