package com.letgee.application.handle;import com.letgee.application.common.param.dto.UserEntrienceDTO;import com.letgee.application.common.param.vo.UserVO;import com.letgee.infrastructure.repository.po.RhSysUser;import javax.annotation.Resource;import java.util.Objects;public class UserHandler {        public static RhSysUser convertDTO2PO(UserEntrienceDTO userEntrienceDTO){        RhSysUser rhSysUser = new RhSysUser();        rhSysUser.setAccount(userEntrienceDTO.getAccount());        rhSysUser.setAvatar(userEntrienceDTO.getAvatar());        rhSysUser.setEmail(userEntrienceDTO.getEmail());        rhSysUser.setMobile(userEntrienceDTO.getMobile());        rhSysUser.setNickName(userEntrienceDTO.getNick_name());        rhSysUser.setRealName(userEntrienceDTO.getReal_name());        rhSysUser.setDeptId(userEntrienceDTO.getDept_id());        rhSysUser.setUserType(userEntrienceDTO.getRole());        rhSysUser.setSex(userEntrienceDTO.getSex());        rhSysUser.setRemark(userEntrienceDTO.getRemark());        return rhSysUser;    }    public static UserVO convertPO2VO(RhSysUser user){        return UserVO.builder()                .id(user.getId())                .user_type(user.getUserType())                .status(user.getStatus())                .account(user.getAccount())                .phone_number(Objects.isNull(user.getMobile())?"":user.getMobile())                .build();    }    }